mapper.select:
select

from labeled_visuals

     and id = #{id}
     and original_file_name like concat('%', #{originalFileName}, '%')
     and file_type = #{fileType}
     and status = #{status}
     and user_id = #{userId}
     and batch_id = #{batchId}

order by created_time desc

mapper.update:
update labeled_visuals
original_file_name = #{originalFileName}, file_type = #{fileType}, original_file_url = #{originalFileUrl}, annotated_file_url = #{annotatedFileUrl}, detection_results = #{detectionResults}, detection_count = #{detectionCount}, inference_time = #{inferenceTime}, status = #{status}, created_time = #{createdTime}, updated_time = #{updatedTime}, user_id = #{userId}, batch_id = #{batchId},
where id = #{id}
